import React, { useState } from 'react'

import { BoardBody, Square, Line, Knight } from './styled'

const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content: ''}))

function Board() {
  const [board, setBoard] = useState(makeBoard)

  const setKnight = ({target}) => {
    const [line, column] = target.name.split('-').map(Number)
    const newBoard = board;
    newBoard[line][column].knight = true;

    console.log(newBoard)
    setBoard(newBoard)
  }

  return (
    <BoardBody >
    {board.map((line, l) => (
      <Line key={`linha ${l}`}>
        {line.map(({knight, content}, c) => 
          !knight ? (
            <Square
              key={`square ${l, c}`}
              name={`${l}-${c}`}
              onClick={ setKnight }
            >
              {content}
            </Square>
            ) : (
            <Knight
              key={`square ${l, c}`}
              name={`${l}-${c}`}
            >
              {content}
            </Knight>
          ))}
      </Line>))}
    </BoardBody>
  )
}

export default Board;
