import React, { useState } from 'react'
import { getMatrixPosition, toLetter } from '../../utils/convert';
import resolveTour from '../../utils/resolveTour';

import { BoardBody, Square, Line, Knight } from './styled'


function Board() {
  const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content:''}))
  const [board, setBoard] = useState(makeBoard);
  const [initialPosition, setInitialPosition] = useState();

  const setKnight = (square, playing = false) => {
    const [line, column] = square.split('-').map(Number);

    const newBoard = board.map((row, l) => row
    .map((col, c) => {
      if (l == line && column == c) {
         return {
            knight: true,
            visited: true,
            content: 'x',
          }
      }
      return {
        ...col,
        knight: false,
      }
    }
    ))
    setInitialPosition({line, column})
    setBoard(newBoard)
  }

  const play = ({line, column}) => {
    const initSquare = `${toLetter(column)}${line + 1}`;

    const result = resolveTour(initSquare).map(getMatrixPosition)
    console.log(result)

    let count = 0;

    setInterval(() => {
      console.log(result[count])
      setKnight(result[count], true)

      if(count === result.length -1) clearInterval();
      else count += 1
    }, 1000)
  }

  return (
    <BoardBody >
    {board.map((line, l) => (
      <Line key={`linha ${l}`}>
        {line.map(({knight, content}, c) => 
          !knight ? (
            <Square
              key={`square ${l, c}`}
              id={`${l}-${c}`}
              onClick={ ({target: {id}}) => setKnight(id) }
            >
              {content}
            </Square>
            ) : (
            <Knight
              key={`square ${l, c}`}
              id={`${l}-${c}`}
            />
          ))}
      </Line>))}
      <button
        onClick={ () => play(initialPosition) }
      >
        play
      </button>
      </BoardBody>
  )
}

export default Board;
