import React, { useState } from 'react'

import { BoardBody, Square, Line, Knight } from './styled'

const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content: ''}))

function Board() {
  const [board, setBoard] = useState(makeBoard)

  return (
    <BoardBody >
    {board.map((line) => (
      <Line>
        {line.map(({knight, content}) => 
          knight ? (
            <Square>
              {content}
            </Square>
            ) : (
            <Knight >
              {content}
            </Knight>
          ))}
      </Line>))}
    </BoardBody>
  )
}

export default Board;
