import React, { useState } from 'react'

import { BoardBody, Square, Line, Knight } from './styled'


function Board() {
  const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content:''}))
  const [board, setBoard] = useState(makeBoard)

  const setKnight = ({target: {id}}) => {
    const [line, column] = id.split('-').map(Number)

    const newBoard = board.map((row, l) => row
    .map((col, c) => {
      console.log('cliquem em',line, column, 'é igual a', l, c)
      if (row == line && column == c) {
        console.log('ENTREEEI NO IF')
        return {
          knight: true,
          constent: 'X',
          visited: true,
        }
      }
      return col;
    }
    ))

    setBoard(newBoard)
  }

  return (
    <BoardBody >
    {board.map((line, l) => (
      <Line key={`linha ${l}`}>
        {line.map(({knight, content}, c) => 
          !knight ? (
            <Square
              key={`square ${l, c}`}
              id={`${l}-${c}`}
              onClick={ setKnight }
            >
              {content}
            </Square>
            ) : (
            <Knight
              key={`square ${l, c}`}
              id={`${l}-${c}`}
            >
              {content}
            </Knight>
          ))}
      </Line>))}
    </BoardBody>
  )
}

export default Board;
