import React, { useState } from 'react'

import { BoardBody, Square, Line } from './styled'

const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content: 'as'}))

function Board() {
  const [board, setBoard] = useState(makeBoard)

  return (
    <BoardBody >
    {board.map((line) => (
      <Line>
        {line.map(column => <Square>{column.content}</Square>)}
      </Line>))}
    </BoardBody>
  )
}

export default Board;
