import styled from 'styled-components';
import horse from '../../imgs/horse.png'

export const BoardBody = styled.main`
  align-items: center;
  display: flex;
  flex-direction: column-reverse;
`;

export const Square = styled.div`
  width: 50px;
  height: 50px;
  color: red;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 900;
`;

export const Knight = styled.div`
  width: 50px;
  height: 50px;
  background-image: url(${horse});
  background-size: cover;
  background-position: center;
`;

export const Line = styled.div`
  display: flex;
  flex-direction: row;

  :nth-child(even) div:nth-child(even),
  :nth-child(ODD) div:nth-child(ODD){
    background-color: white;
    color: teal;
    filter: invert(100%);
  }
`;

export const Move = styled.span`
  padding: 5px;
  margin: 5px;
  :nth-child(odd){
    background: rgb(200, 200, 200);
  }
`;

export const Movements = styled.p`
  width: 80%;
  line-height: 2;
  display: flex;
  flex-wrap: wrap;
`;
