import React, { useState } from 'react'
import { toLetter } from '../../utils/convert';
import resolveTour from '../../utils/resolveTour';

import { BoardBody, Square, Line, Knight } from './styled'


function Board() {
  const makeBoard = Array(8).fill(Array(8).fill({visited: false, knight: false, content:''}))
  const [board, setBoard] = useState(makeBoard);
  const [initialPosition, setInitialPosition] = useState();

  const setKnight = ({target: {id}}) => {
    const [line, column] = id.split('-').map(Number);

    const newBoard = board.map((row, l) => row
    .map((col, c) => {
      console.log('cliquem em',line, column, 'é igual a', l, c, l == line, column == c)
      if (l == line && column == c) {
        console.log('ENTREEEI NO IF')
        return {
          knight: true,
          content: 'X',
          visited: true,
        }
      }
      return {
        ...col,
        knight: false,
      }
    }
    ))
    setInitialPosition({line, column})
    setBoard(newBoard)
  }

  const play = ({line, column}) => {
    const initSquare = `${toLetter(column)}-${line + 1}`

    console.log(resolveTour(initSquare));
  }

  return (
    <BoardBody >
    {board.map((line, l) => (
      <Line key={`linha ${l}`}>
        {line.map(({knight, content}, c) => 
          !knight ? (
            <Square
              key={`square ${l, c}`}
              id={`${l}-${c}`}
              onClick={ setKnight }
            >
              {content}
            </Square>
            ) : (
            <Knight
              key={`square ${l, c}`}
              id={`${l}-${c}`}
            />
          ))}
      </Line>))}
      <button
        onClick={ () => play(initialPosition) }
      >
        play
      </button>
      </BoardBody>
  )
}

export default Board;
